INSERT INTO `tb_artist` VALUES (16, 'Vexento', 0, 'http://localhost:9000/vibe-music-data/artists/bf682fae-e656-44ec-9d4e-f75bd3411a90-blob', NULL, '挪威', 'Vexento，一位来自挪威的崭露头角电子音乐才子，本名Alexander Hansen，1997年诞生，以他独特的音乐魅力征服了无数听众的心。他的曲风多变，既正面又俏皮，总能在不经意间带给人们意想不到的惊喜。', NULL);
INSERT INTO `tb_artist` VALUES (18, 'G.E.M.邓紫棋', 1, 'http://localhost:9000/vibe-music-data/artists/71bff6e1-e4cf-41ca-bfce-29a1c8439651-blob', '1991-08-16', '中国', '邓紫棋（Gloria Tang Tsz-Kei），又名G.E.M.，是中国香港的创作型女歌手、词曲创作人。2008年，年仅16岁的邓紫棋出道，并于同年10月发行首张EP《G.E.M.》，凭借这张作品迅速在香港乐坛崭露头角，获得各大颁奖礼新人金奖。', NULL);
INSERT INTO `tb_artist` VALUES (23, '徐梦圆', 0, 'http://localhost:9000/vibe-music-data/artists/bf87e7aa-7ed4-4fa9-97bb-c601a03449b4-blob', '1992-10-22', '中国', '徐梦圆，音乐制作人，擅长中国风电子音乐的制作，2018年，作为首位在CCTV1官方节目中表演的90后DJ，被央视誉为“中国新时代电音领军人物”；同年，获PYRO百大DJ榜单冠军。', NULL);
