INSERT INTO `tb_playlist` VALUES (8, '【韩流】韩国流行热门歌曲', 'http://localhost:9000/vibe-music-data/playlists/022ae029-ba8e-4dab-b2fc-36a85c271512-blob', NULL, '韩国合唱团数不胜数 有些虽然解散 但仍给我们留下了很多美好回忆 有些刚刚出道 正向着“一位”努力前进 有些天团正站在世界的舞台 散发着无穷的魅力 不论你什么职业 任何年龄 不论你喜欢什么团队 深深地...', '韩国流行');
INSERT INTO `tb_playlist` VALUES (9, '世界级古典钢琴曲音乐珍藏', 'http://localhost:9000/vibe-music-data/playlists/6959d662-dc4b-4063-9df1-36ba0c19255a-blob', NULL, '钢琴，乐器之皇，钢琴文化发展至今，已经有不计其数的脍炙人口的钢琴曲流传下来，其中一些流传至今，仍是社会上流人士的...', '古典');
INSERT INTO `tb_playlist` VALUES (10, '欧美氛围R&B ｜ 听节奏布鲁斯才是正经事', 'http://localhost:9000/vibe-music-data/playlists/8c74ab2f-9053-4ffc-b15a-169c2234d5a3-blob', NULL, '节奏布鲁斯（英语：Rhythm and Blues，简称：R&B或RnB，中国台湾、中国港澳、马新称作节奏蓝调 ），又称节奏怨曲。', '节奏布鲁斯');
INSERT INTO `tb_playlist` VALUES (11, '那些只听前奏就会上瘾的华语流行歌曲', 'http://localhost:9000/vibe-music-data/playlists/c2d1ebca-5af9-4d3f-947d-c67ce8193712-blob', NULL, '趁你现在还有时间 仅你自己最大的努力 努力做成你最想做的那件事 成为你最想成为的那种人 过着你最想过的那种生活 ', '华语流行');
